require 'vendor/autoload.php';
use smnjan\Auth;
$auth = new Auth();
if ($auth->isLoggedIn()){
    header('Location: dashboard.php');
    exit;
}
$error = false;
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    if (isset($_POST['password'])){
        $error = false;
        $password = $_POST['password'];
        if ($auth->login($password)) {
            header('Location: dashboard.php');
            exit;
        } else {
            $error = true;
        }

    }
} else {
    $error = false;
}
?>
